//import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { View, StyleSheet, Text, FlatList, Image } from 'react-native';

import Helper from './components'

// it's a function bro.
const list = () => {

  const li = [
               { 'name' : 'Friend #1'},
               { 'name' : 'Friend #2'},
               { 'name' : 'Friend #3'},
               { 'name' : 'Friend #4'},
               { 'name' : 'Friend #5'},
               { 'name' : 'Friend #6'},
             ]

  return (
    <View>
      <Text style={ styles.textTitle }>Mobile Apps</Text>
      <Helper text='One' imageSource={require('./assets/mountain.jpg')} />
      <Helper text='Two' />
      <Helper text='Six' />
      <Helper text='Ten' />
      <FlatList 
        data={li} 
        renderItem={ (element)=>{
          return (
            <Text style={ styles.textSub }>
              {element.index+1}.) Name - {element.item.name}
            </Text>
          )
        }}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  textTitle : {
    fontSize : 40,
    color    : 'white',
    textAlign : 'center',
    backgroundColor : 'black',
  },

  textSub : {
    fontSize : 20,
    marginVertical : 10,
    fontStyle : 'italic',
    fontWeight: 'bold',
    color : 'orange',
  }

});


export default list;
